mapper.update:
UPDATE board
SET del_date = now()
WHERE bno = #{bno}

UPDATE board
SET   view_cnt = view_cnt + 1
WHERE bno = #{bno}

UPDATE board
SET   title = #{title}
  , content = #{content}
  , up_date = now()
WHERE bno = #{bno}

update board
set comment_cnt = comment_cnt + #{cnt}
where bno= #{bno}
